import { MailOutline, Phone } from "@material-ui/icons";
import styled from "styled-components";
import { mobile } from "../responsive";

const Container = styled.div`
  display: flex;
  ${mobile({ flexDirection: "column" })}
  background-color: #000;
  color: #dadde0;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1``;

const Desc = styled.p`
  margin: 20px 0px;
`;

const Center = styled.div`
  flex: 1;
  padding: 20px;
  ${mobile({ display: "none" })}
`;

const Title = styled.h3`
  margin-bottom: 30px;
`;

const Right = styled.div`
  flex: 1;
  padding: 20px;
  flex-direction: column;
  ${mobile({ backgroundColor: "#fff8f8" })}
`;

const ContactItem = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`;

const Payment = styled.img`
  width: 50%;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo style={{ color: "white" }}>
          VEL<span style={{ color: "#15DB4C" }}>POX SH</span>OP
        </Logo>
        <Desc>
          Yüksek müşteri memnuniyeti ve hızlı teslimat anlayışı ile sizlere
          hizmet vermekten mutluluk duyarız. Ürünlerde oluşabilecek herhangi bir
          problemde yahut sorularınızda bize ulaşmaktan çekinmeyin. Size Hizmet
          etmekten memnuniyet duyarız.
        </Desc>
      </Left>
      <Center></Center>
      <Right>
        <Title>Contact</Title>

        <ContactItem>
          <Phone style={{ marginRight: "10px" }} /> +90 312 123 45 67
        </ContactItem>
        <ContactItem>
          <MailOutline
            style={{
              background: " linear-gradient(to top right, #000000, #16db4c)",
            }}
          />
        </ContactItem>
      </Right>
    </Container>
  );
};

export default Footer;
