import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import { useState } from "react";
import styled from "styled-components";
import { sliderItems } from "../data";
import { mobile } from "../responsive";
import React, { useRef } from "react";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  background-color: #f5f5f5;
  overflow: hidden;
  ${mobile({ display: "none" })}
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #6b75d4;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  z-index: 2;
`;

const Wrapper = styled.div`
  height: 100%;
  display: flex;
  transition: all 1.5s ease;
  transform: translateX(${(props) => props.translateValue}px);
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  color: #141414;
`;

const ImgContainer = styled.div`
  height: 70%;
  flex: 1;
  background-color: #8db7dd;
  border-radius: 100px;
  box-shadow: 4px 0px 2px 0px rgba(64, 81, 59, 0.5);
`;

const Image = styled.img`
  height: 80%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 42px;
`;

const Desc = styled.p`
  margin: 50px 0px;
  font-size: 18px;
  font-weight: 500;
  letter-spacing: 3px;
`;

const Button = styled.button`
  padding: 10px;
  font-size: 18px;
  background: #6b75d4;
  box-shadow: 2px 0px 2px 0px rgba(64, 81, 59, 0.5);
  color: #060907;
  cursor: pointer;
  border-radius: 15px;
  border-color: #faf1e6;
  &:hover {
    background: #8db7dc;
  }
`;

const handleScrollToProducts = () => {
  window.scrollBy({ top: 800, behavior: "smooth" });
};

const Slider = () => {
  const [translateValue, setTranslateValue] = useState(0);
  const slideIndex = useRef(0);

  const handleClick = (direction) => {
    const width = window.innerWidth;
    if (direction === "left") {
      slideIndex.current = slideIndex.current > 0 ? slideIndex.current - 1 : 2;
      setTranslateValue(-slideIndex.current * width);
    } else {
      slideIndex.current = slideIndex.current < 2 ? slideIndex.current + 1 : 0;
      setTranslateValue(-slideIndex.current * width);
    }
  };

  return (
    <Container>
      <Arrow direction="left" onClick={() => handleClick("left")}>
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper translateValue={translateValue}>
        {sliderItems.map((item) => (
          <Slide bg={item.bg} key={item.id}>
            <ImgContainer>
              <Image style={{ width: "700px" }} src={item.img} />
            </ImgContainer>
            <InfoContainer>
              <Title>{item.title}</Title>
              <Desc>{item.desc}</Desc>
              <Button onClick={handleScrollToProducts}>MaÄŸzaya git</Button>
            </InfoContainer>
          </Slide>
        ))}
      </Wrapper>
      <Arrow direction="right" onClick={() => handleClick("right")}>
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
};

export default Slider;
